import React, { useState } from 'react'


const TutorHeader = () => {
    const [tutor_username, setTutor_Username] = useState("");
    const [tutor_phoneNumber, setTutor_PhoneNumber] = useState("");
    const [tutor_email, setTutor_Email] = useState("");
    const [tutor_subject, setTutor_Subject] = useState("");
    const [tutor_classes, setTutor_Classes] = useState("");
    const [tutor_address, setTutor_Address] = useState("");
    const [tutor_errMsg, setTutor_ErrMsg] = useState("");
    const [tutor_successMsg, setTutor_SuccessMsg] = useState("");
    let url="https://script.google.com/macros/s/AKfycbwkPYpaC3gEvBJlOoyoxVYAx9CX9uVht8lbRPggsOwc_Xkm2Xs9rJ9lPeUoOc6ZNWiVCQ/exec"

    let form= document.querySelector('#tutor-form');
    form.addEventListener("submit", (e)=>{
        e.preventDefault();
        let tutordata= new FormData(form);
        fetch('url', {
            method: 'POST',
            body: tutordata,
          })
          .then((res)=> res.text())
          .then((finalRes)=> console.log(finalRes))
    });

    // const handleTutor_Send = (e) => {
    //   e.preventDefault();
    //   // Your form validation and submission logic here

    //   // Prepare form data

    //   const formData = new FormData();
    //   formData.append('tutor_username', tutor_username);
    //   formData.append('tutor_email', tutor_email);
    //   formData.append('tutor_phoneNumber', tutor_phoneNumber);
    //   formData.append('tutor_subject', tutor_subject);
    //   formData.append('tutor_classes', tutor_classes);
    //   formData.append('tutor_address', tutor_address);

    //   // Send form data to Google Apps Script endpoint
    //   fetch('YOUR_GOOGLE_APPS_SCRIPT_URL', {
    //     method: 'POST',
    //     body: formData,
    //   })
    //   .then(response => {
    //     if (response.ok) {
    //       // Handle successful form submission
    //       setTutor_SuccessMsg(`Thank you, ${tutor_username}! Your form has been submitted successfully.`);
    //       setTutor_ErrMsg("");
    //       // Clear form fields
    //       setTutor_Username("");
    //       setTutor_PhoneNumber("");
    //       setTutor_Email("");
    //       setTutor_Subject("");
    //       setTutor_Classes("");
    //       setTutor_Address("");
    //     } else {
    //       // Handle error
    //       setTutor_SuccessMsg("");
    //       setTutor_ErrMsg("Oops! Something went wrong. Please try again later.");
    //     }
    //   })
    //   .catch(error => {
    //     // Handle network error
    //     setTutor_SuccessMsg("");
    //     setTutor_ErrMsg("Network error. Please try again later.");
    //   });
    // };


  return (
    <div class="relative p-4 w-full max-w-md max-h-full">
          <div class="relative bg-white rounded-lg shadow dark:bg-gray-700">
              {tutor_errMsg && (
                <p className="py-3 bg-gray-100 shadow-shadowOne text-center text-red-500 text-base tracking-wide animate-bounce">
                  {tutor_errMsg} </p>
              )}
              {tutor_successMsg && (
                <p className="py-3 bg-gray-100 shadow-shadowOne text-center text-green-500 text-base tracking-wide animate-bounce">
                  {tutor_successMsg} </p>
              )}
              <div class="flex items-center justify-between py-0 px-4 md:p-5 border-b rounded-t dark:border-gray-600">
                <h3 class="text-xl font-semibold text-gray-900 dark:text-white">
                  Create Tutor Account Now!
                </h3>
                <button type="button" class="end-2.5 text-gray-400 bg-transparent hover:bg-gray-200 hover:text-gray-900 rounded-lg text-sm w-8 h-8 ms-auto inline-flex justify-center items-center dark:hover:bg-gray-600 dark:hover:text-white" data-modal-hide="tutor-modal">
                  <svg class="w-3 h-3" aria-hidden="true" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 14 14">
                    <path stroke="currentColor" stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="m1 1 6 6m0 0 6 6M7 7l6-6M7 7l-6 6" />
                  </svg>
                  <span class="sr-only">Close modal</span>
                </button>
              </div>
              <div class="p-4 md:p-5">
                <form class="max-w-sm mx-auto" method="POST" action="" id="tutor-form">
                  <div class="mb-3">
                    <label for="username" class="block mb-2 text-sm font-medium text-gray-900 dark:text-white">Your Username</label>
                    <input onChange={(e) => setTutor_Username(e.target.value)}
                      value={tutor_username}
                      className={`${tutor_errMsg === "Username is required!" &&
                        "outline-designColor"
                        } contactInput`} name="tutor_username" type="text" id="username" class="shadow-sm bg-gray-50 border border-gray-300 text-gray-900 text-sm rounded-lg focus:ring-blue-500 focus:border-blue-500 block w-full p-2.5 dark:bg-gray-700 dark:border-gray-600 dark:placeholder-gray-400 dark:text-white dark:focus:ring-blue-500 dark:focus:border-blue-500 dark:shadow-sm-light" placeholder="Username" required />
                  </div>
                  <div class="mb-3">
                    <label for="email" class="block mb-2 text-sm font-medium text-gray-900 dark:text-white">Your email</label>
                    <input onChange={(e) => setTutor_Email(e.target.value)}
                      value={tutor_email}
                      className={`${tutor_errMsg === "Please give your Email!" &&
                        "outline-designColor"
                        } contactInput`} name="tutor_email" type="email" id="email" class="shadow-sm bg-gray-50 border border-gray-300 text-gray-900 text-sm rounded-lg focus:ring-blue-500 focus:border-blue-500 block w-full p-2.5 dark:bg-gray-700 dark:border-gray-600 dark:placeholder-gray-400 dark:text-white dark:focus:ring-blue-500 dark:focus:border-blue-500 dark:shadow-sm-light" placeholder="Email" required />
                  </div>
                  <div class="mb-3">
                    <label for="phoneNumber" class="block mb-2 text-sm font-medium text-gray-900 dark:text-white">Your Phone Number</label>
                    <input onChange={(e) => setTutor_PhoneNumber(e.target.value)}
                      value={tutor_phoneNumber}
                      className={`${tutor_errMsg === "Phone number is required!" &&
                        "outline-designColor"
                        } contactInput`} name="tutor_phoneNumber" type="tel" id="phoneNumber" class="shadow-sm bg-gray-50 border border-gray-300 text-gray-900 text-sm rounded-lg focus:ring-blue-500 focus:border-blue-500 block w-full p-2.5 dark:bg-gray-700 dark:border-gray-600 dark:placeholder-gray-400 dark:text-white dark:focus:ring-blue-500 dark:focus:border-blue-500 dark:shadow-sm-light" placeholder="Phone number" required />
                  </div>
                  <div class="mb-3">
                    <label for="address" class="block mb-2 text-sm font-medium text-gray-900 dark:text-white">Your Address</label>
                    <input onChange={(e) => setTutor_Address(e.target.value)}
                      value={tutor_address}
                      className={`${tutor_errMsg === "Please enter your address" &&
                        "outline-designColor"
                        } contactInput`} name="tutor_address" type="text" id="address" class="shadow-sm bg-gray-50 border border-gray-300 text-gray-900 text-sm rounded-lg focus:ring-blue-500 focus:border-blue-500 block w-full p-2.5 dark:bg-gray-700 dark:border-gray-600 dark:placeholder-gray-400 dark:text-white dark:focus:ring-blue-500 dark:focus:border-blue-500 dark:shadow-sm-light" placeholder="Address" required />
                  </div>
                  <div class="mb-3">
                    <label for="classes" class="block mb-2 text-sm font-medium text-gray-900 dark:text-white">Classes you teach!</label>
                    <input onChange={(e) => setTutor_Classes(e.target.value)}
                      value={tutor_classes}
                      className={`${tutor_errMsg === "Classes you are interested in!" &&
                        "outline-designColor"
                        } contactInput`} name="tutor_classes" type="text" id="classes" class="shadow-sm bg-gray-50 border border-gray-300 text-gray-900 text-sm rounded-lg focus:ring-blue-500 focus:border-blue-500 block w-full p-2.5 dark:bg-gray-700 dark:border-gray-600 dark:placeholder-gray-400 dark:text-white dark:focus:ring-blue-500 dark:focus:border-blue-500 dark:shadow-sm-light" required />
                  </div>
                  <div class="mb-3">
                    <label for="subject" class="block mb-2 text-sm font-medium text-gray-900 dark:text-white">Subject</label>
                    <input onChange={(e) => setTutor_Subject(e.target.value)}
                      value={tutor_subject}
                      className={`${tutor_errMsg === "Subject you are interested in!" &&
                        "outline-designColor"
                        } contactInput`} name="tutor_subject" type="text" id="subject" class="shadow-sm bg-gray-50 border border-gray-300 text-gray-900 text-sm rounded-lg focus:ring-blue-500 focus:border-blue-500 block w-full p-2.5 dark:bg-gray-700 dark:border-gray-600 dark:placeholder-gray-400 dark:text-white dark:focus:ring-blue-500 dark:focus:border-blue-500 dark:shadow-sm-light" required />
                  </div>
                  <button name="submit" type="submit" class="text-white bg-blue-700 hover:bg-blue-800 focus:ring-4 focus:outline-none focus:ring-blue-300 font-medium rounded-lg text-sm px-5 py-2.5 text-center dark:bg-blue-600 dark:hover:bg-blue-700 dark:focus:ring-blue-800">Register new account</button>
                </form>
              </div>
