import React, { useState } from 'react'
import background from '../../assets/images/bg.png';


const Hero = () => {
  const [tutor_username, setTutor_Username] = useState("");
  const [tutor_phoneNumber, setTutor_PhoneNumber] = useState("");
  const [tutor_email, setTutor_Email] = useState("");
  const [tutor_subject, setTutor_Subject] = useState("");
  const [tutor_classes, setTutor_Classes] = useState("");
  const [tutor_address, setTutor_Address] = useState("");
  const [tutor_errMsg, setTutor_ErrMsg] = useState("");
  const [tutor_successMsg, setTutor_SuccessMsg] = useState("");


  const [stu_username, setStu_Username] = useState("");
  const [stu_phoneNumber, setStu_PhoneNumber] = useState("");
  const [stu_email, setStu_Email] = useState("");
  const [stu_subject, setStu_Subject] = useState("");
  const [stu_classes, setStu_Classes] = useState("");
  const [stu_address, setStu_Address] = useState("");
  const [stu_errMsg, setStu_ErrMsg] = useState("");
  const [stu_successMsg, setStu_SuccessMsg] = useState("");




  // ========== Email Validation end here ================
  let stu_data = document.getElementById("#student-modal");

  // ========== Email Validation start here ==============
  let emailValidation = () => {
    return String(stu_email)
      .toLocaleLowerCase()
      .match(/^\w+([-]?\w+)*@\w+([-]?\w+)*(\.\w{2,3})+$/);
  };

  const handleStu_Send = (e) => {
    e.preventDefault();
    if (stu_username === "") {
      setStu_SuccessMsg("")
      setStu_ErrMsg("Username is required!");
    } else if (stu_phoneNumber === "") {
      setStu_SuccessMsg("")
      setStu_ErrMsg("Phone number is required!");
    } else if (stu_email === "") {
      setStu_SuccessMsg("")
      setStu_ErrMsg("Please give your Email!");
    } else if (!emailValidation(stu_email)) {
      setStu_SuccessMsg("")
      setStu_ErrMsg("Give a valid Email!");
    } else if (stu_subject === "") {
      setStu_SuccessMsg("")
      setStu_ErrMsg("Subject you are interested in!");
    } else if (stu_classes === "") {
      setStu_SuccessMsg("")
      setStu_ErrMsg("Classes you want to teach!");
    } else if (stu_address === "") {
      setStu_SuccessMsg("")
      setStu_ErrMsg("Please provide your Address");
    } else {
      let data = new FormData(stu_data);
      // The FormData() method creates a new FormData object that contains the values of all the form fields.
      fetch('https://script.google.com/u/1/home/projects/15CPW9GgySodCemfWtHZXCwHS18SbP4oqtYCswGxDH4tudAih3qvpUkwc/edit', {
        method: "POST",
        body: data
      })
        // The fetch() method is used to make a request to the server and retrieve data.
        // This is an example API endpoint. Replace it with the actual URL for the API endpoint you want to use.
        .then(res => res.text())
      // The .then() method is used to handle the response from the server.
      // The response is converted to text using the res.text() method.

      setStu_SuccessMsg(
        `Thank you dear ${stu_username}, Your Messages has been sent Successfully!`
      );
      setStu_ErrMsg("");
      setStu_Username("");
      setStu_PhoneNumber("");
      setStu_Email("");
      setStu_Subject("");
      setStu_Classes("");
      setStu_Address("");
    }

  }

  // ========== Email Validation end here ================
  let tutor_data = document.querySelector("#tutor-modal");

  // ========== Email Validation start here ==============
  emailValidation = () => {
    return String(tutor_email)
      .toLocaleLowerCase()
      .match(/^\w+([-]?\w+)*@\w+([-]?\w+)*(\.\w{2,3})+$/);
  };

  const handleTutor_Send = (e) => {
    e.preventDefault();
    if (tutor_username === "") {
      setTutor_SuccessMsg("")
      setTutor_ErrMsg("Username is required!");
    } else if (tutor_phoneNumber === "") {
      setTutor_SuccessMsg("")
      setTutor_ErrMsg("Phone number is required!");
    } else if (tutor_email === "") {
      setTutor_SuccessMsg("")
      setTutor_ErrMsg("Please give your Email!");
    } else if (!emailValidation(tutor_email)) {
      setTutor_SuccessMsg("")
      setTutor_ErrMsg("Give a valid Email!");
    } else if (tutor_subject === "") {
      setTutor_SuccessMsg("")
      setTutor_ErrMsg("Subject you are interested in!");
    } else if (tutor_classes === "") {
      setTutor_SuccessMsg("")
      setTutor_ErrMsg("Classes you want to teach!");
    } else if (tutor_address === "") {
      setTutor_SuccessMsg("")
      setTutor_ErrMsg("Please provide your Address");
    } else {
      let data = new FormData(tutor_data);
      // The FormData() method creates a new FormData object that contains the values of all the form fields.
      fetch('https://script.google.com/u/1/home/projects/15CPW9GgySodCemfWtHZXCwHS18SbP4oqtYCswGxDH4tudAih3qvpUkwc/edit', {
        method: "POST",
        body: data
      })
        // The fetch() method is used to make a request to the server and retrieve data.
        // This is an example API endpoint. Replace it with the actual URL for the API endpoint you want to use.
        .then(res => res.text())
      // The .then() method is used to handle the response from the server.
      // The response is converted to text using the res.text() method.

      setTutor_SuccessMsg(
        `Thank you dear ${tutor_username}, Your Messages has been sent Successfully!`
      );
      setTutor_ErrMsg("");
      setTutor_Username("");
      setTutor_PhoneNumber("");
      setTutor_Email("");
      setTutor_Subject("");
      setTutor_Classes("");
      setTutor_Address("");
    }

  };
  return (


    <section class="bg-center bg-no-repeat bg-blend-multiply pt-20 mb-10 pb-10 dark:bg-gray-800 dark:mb-0 dark:pb-20" style={{ backgroundImage: `url(${background})` }}>
      <div class="pt-8 px-4 mx-auto max-w-screen-xl text-center justify-center items-center lg:pt-16">
        <h3 class='my-6 text-xl tracking-wide leading-none text-blue-600 dark:text-white'> Join Our Platform and Ignite Your Passion for Teaching! </h3>
        <h1 class="my-12 text-5xl font-extrabold tracking-tight leading-none text-other md:text-3xl lg:text-5xl dark:text-white">  <span class="text-blue-700">Indian Tutor Expert! </span> for the work you have passion  </h1>
        <p class="my-10 text-lg font-extralight text-other lg:text-2xl sm:px-16 lg:px-48 dark:text-gray-400">From foundational subjects to advanced concepts, our tutors provide comprehensive support to help students excel in their studies. </p>
        <div class="flex flex-col space-y-2 sm:flex-row sm:justify-center sm:items-center sm:space-y-0">
          <button data-modal-target="tutor-modal" data-modal-toggle="tutor-modal" class="inline-flex no-underline sm:mr-0 md:mr-2 px-10 py-5 justify-center items-center text-base font-medium text-center text-white rounded-full bg-blue-700 hover:bg-blue-800 focus:ring-4 focus:ring-blue-300 dark:focus:ring-blue-900">
            Register as Tutor
          </button>
          <button data-modal-target="student-modal" data-modal-toggle="student-modal" class="inline-flex no-underline px-10 py-5 justify-center items-center text-base font-medium text-center text-white rounded-full bg-blue-700 hover:bg-blue-800 focus:ring-4 focus:ring-blue-300 dark:focus:ring-blue-900">
            Register as Student
          </button>
        </div>
      </div>


      <div id="tutor-modal" tabindex="-1" aria-hidden="true" class="hidden overflow-y-auto overflow-x-hidden fixed top-0 right-0 left-0 z-50 justify-center items-center w-full md:inset-0 h-[calc(100%-1rem)] max-h-full">
        <div class="relative p-4 w-full max-w-md max-h-full">
          <div class="relative bg-white rounded-lg shadow dark:bg-gray-700">
            <form method="POST" action="" name="tutor-form">
              {tutor_errMsg && (
                <p className="py-3 bg-gray-100 shadow-shadowOne text-center text-red-500 text-base tracking-wide animate-bounce">
                  {tutor_errMsg} </p>
              )}
              {tutor_successMsg && (
                <p className="py-3 bg-gray-100 shadow-shadowOne text-center text-green-500 text-base tracking-wide animate-bounce">
                  {tutor_successMsg} </p>
              )}
              <div class="flex items-center justify-between py-0 px-4 md:p-5 border-b rounded-t dark:border-gray-600">
                <h3 class="text-xl font-semibold text-gray-900 dark:text-white">
                  Create Tutor Account Now!
                </h3>
                <button type="button" class="end-2.5 text-gray-400 bg-transparent hover:bg-gray-200 hover:text-gray-900 rounded-lg text-sm w-8 h-8 ms-auto inline-flex justify-center items-center dark:hover:bg-gray-600 dark:hover:text-white" data-modal-hide="tutor-modal">
                  <svg class="w-3 h-3" aria-hidden="true" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 14 14">
                    <path stroke="currentColor" stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="m1 1 6 6m0 0 6 6M7 7l6-6M7 7l-6 6" />
                  </svg>
                  <span class="sr-only">Close modal</span>
                </button>
              </div>
              <div class="p-4 md:p-5">
                <form class="max-w-sm mx-auto">
                  <div class="mb-3">
                    <label for="username" class="block mb-2 text-sm font-medium text-gray-900 dark:text-white">Your Username</label>
                    <input onChange={(e) => setTutor_Username(e.target.value)}
                      value={tutor_username}
                      className={`${tutor_errMsg === "Username is required!" &&
                        "outline-designColor"
                        } contactInput`} name="username" type="text" id="username" class="shadow-sm bg-gray-50 border border-gray-300 text-gray-900 text-sm rounded-lg focus:ring-blue-500 focus:border-blue-500 block w-full p-2.5 dark:bg-gray-700 dark:border-gray-600 dark:placeholder-gray-400 dark:text-white dark:focus:ring-blue-500 dark:focus:border-blue-500 dark:shadow-sm-light" placeholder="Username" required />
                  </div>
                  <div class="mb-3">
                    <label for="email" class="block mb-2 text-sm font-medium text-gray-900 dark:text-white">Your email</label>
                    <input onChange={(e) => setTutor_Email(e.target.value)}
                      value={tutor_email}
                      className={`${tutor_errMsg === "Please give your Email!" &&
                        "outline-designColor"
                        } contactInput`} name="email" type="email" id="email" class="shadow-sm bg-gray-50 border border-gray-300 text-gray-900 text-sm rounded-lg focus:ring-blue-500 focus:border-blue-500 block w-full p-2.5 dark:bg-gray-700 dark:border-gray-600 dark:placeholder-gray-400 dark:text-white dark:focus:ring-blue-500 dark:focus:border-blue-500 dark:shadow-sm-light" placeholder="Email" required />
                  </div>
                  <div class="mb-3">
                    <label for="phoneNumber" class="block mb-2 text-sm font-medium text-gray-900 dark:text-white">Your Phone Number</label>
                    <input onChange={(e) => setTutor_PhoneNumber(e.target.value)}
                      value={tutor_phoneNumber}
                      className={`${tutor_errMsg === "Phone number is required!" &&
                        "outline-designColor"
                        } contactInput`} name="phoneNumber" type="tel" id="phoneNumber" class="shadow-sm bg-gray-50 border border-gray-300 text-gray-900 text-sm rounded-lg focus:ring-blue-500 focus:border-blue-500 block w-full p-2.5 dark:bg-gray-700 dark:border-gray-600 dark:placeholder-gray-400 dark:text-white dark:focus:ring-blue-500 dark:focus:border-blue-500 dark:shadow-sm-light" placeholder="Phone number" required />
                  </div>
                  <div class="mb-3">
                    <label for="address" class="block mb-2 text-sm font-medium text-gray-900 dark:text-white">Your Address</label>
                    <input onChange={(e) => setTutor_Address(e.target.value)}
                      value={tutor_address}
                      className={`${tutor_errMsg === "Please enter your address" &&
                        "outline-designColor"
                        } contactInput`} name="address" type="text" id="address" class="shadow-sm bg-gray-50 border border-gray-300 text-gray-900 text-sm rounded-lg focus:ring-blue-500 focus:border-blue-500 block w-full p-2.5 dark:bg-gray-700 dark:border-gray-600 dark:placeholder-gray-400 dark:text-white dark:focus:ring-blue-500 dark:focus:border-blue-500 dark:shadow-sm-light" placeholder="Address" required />
                  </div>
                  <div class="mb-3">
                    <label for="classes" class="block mb-2 text-sm font-medium text-gray-900 dark:text-white">Classes you teach!</label>
                    <input onChange={(e) => setTutor_Classes(e.target.value)}
                      value={tutor_classes}
                      className={`${tutor_errMsg === "Classes you are interested in!" &&
                        "outline-designColor"
                        } contactInput`} name="classes" type="text" id="classes" class="shadow-sm bg-gray-50 border border-gray-300 text-gray-900 text-sm rounded-lg focus:ring-blue-500 focus:border-blue-500 block w-full p-2.5 dark:bg-gray-700 dark:border-gray-600 dark:placeholder-gray-400 dark:text-white dark:focus:ring-blue-500 dark:focus:border-blue-500 dark:shadow-sm-light" required />
                  </div>
                  <div class="mb-3">
                    <label for="subject" class="block mb-2 text-sm font-medium text-gray-900 dark:text-white">Subject</label>
                    <input onChange={(e) => setTutor_Subject(e.target.value)}
                      value={tutor_subject}
                      className={`${tutor_errMsg === "Subject you are interested in!" &&
                        "outline-designColor"
                        } contactInput`} name="subject" type="text" id="subject" class="shadow-sm bg-gray-50 border border-gray-300 text-gray-900 text-sm rounded-lg focus:ring-blue-500 focus:border-blue-500 block w-full p-2.5 dark:bg-gray-700 dark:border-gray-600 dark:placeholder-gray-400 dark:text-white dark:focus:ring-blue-500 dark:focus:border-blue-500 dark:shadow-sm-light" required />
                  </div>
